mapper.select:

		SELECT n_idproduct, t_name, t_brand, n_price, n_quantity
  		FROM classwork."T_Product"
        WHERE n_idproduct = ${n_idproduct};


		INSERT INTO classwork."T_Product"(
            n_idproduct, t_name, t_brand, n_price, n_quantity)
             VALUES (${n_idproduct}, ${t_name}, ${t_brand}, ${n_price}, ${n_quantity});
